import React from 'react'
import styled from 'styled-components'
import Typewriter from 'typewriter-effect';

function Intro (){
  return(
    <MainContainer>
      <Typewriter
        options={{
          strings: ['Hello', 'World'],
          autoStart: true,
          loop: true,
          delay: 1
        }}
      />
    </MainContainer>
  )
}

export default Intro

const MainContainer = styled.div`
  display: inline-block;
  height: 93vh;
  width: 100vh;
  margin-top: 2%;
  margin-left: 3%;
  background-color: red;
  color: white;
  font-size: 1.2rem;
`
