import React from 'react'
import styled from 'styled-components'
import Typewriter from 'typewriter-effect';
import './asd.css'

function Intro (){
  const date = new Date()
  const day = date.getDate()
  const month = date.getDay()
  const year = date.getFullYear()
  return(
    <MainContainer>
      <Typewriter
        onInit={(tw) => {
          tw.changeDelay(50)
          .changeDeleteSpeed(1)
          .typeString(`Fecha [${day}/${month}/${year+1000}]`)
          .pauseFor(2000)
          .typeString('<br>Protocolo de diagnostico penitenciario ADX FLORENCE')
          .pauseFor(2000)
          .typeString('<br>Inicializando  ')
          .changeDelay(600)
          .typeString('. . .')
          .pauseFor(500)
          .changeDelay(50).changeDelay(50)
          .typeString('<br><br><span class="red">¡ADVERTENCIA anulacion manual del sistema de seguridad detectada!</span>')
          .pauseFor(2000)
          .typeString('<br>Accediendo a la base de datos de la prision  ')
          .changeDelay(600)
          .typeString('. . .')
          .pauseFor(500)
          .changeDelay(50)
          .typeString('Listando prisioneros')
          .changeDelay(600)
          .typeString('. . .')
          .pauseFor(500)
          .changeDelay(1)
          .typeString('<br>Franco Lalia     <span class="red">MUERTO</span>')
          .typeString('<br>Nicolas Lemos          MUERTO')
          .typeString('<br>Gian Franco Lombardini     MUERTO')
          .typeString('<br>Gian Franco Lombardini     MUERTO')
          .typeString('<br>Gian Franco Lombardini     MUERTO')
          .typeString('<br>Gian Franco Lombardini     VIVO')
          .typeString('<br>Gian Franco Lombardini     MUERTO')
          .typeString('<br>Gian Franco Lombardini     MUERTO')
          .typeString('<br>Gian Franco Lombardini     MUERTO')
          .start();
        }}/>
    </MainContainer>
  )
}



export default Intro

const MainContainer = styled.div`
  display: inline-block;
  height: 93vh;
  width: 100vh;
  margin-top: 2%;
  margin-left: 3%;
  color: white;
  font-size: 1.2rem;
`
