import React from 'react'
import styled from 'styled-components'
import Typewriter from 'typewriter-effect';

function Intro (){
  const date = new Date()
  const day = date.getDate()
  const month = date.getDay()
  const year = date.getFullYear()
  return(
    <MainContainer>
      <Typewriter
        onInit={(tw) => {
          tw.changeDelay(50)
          .changeDeleteSpeed(1)
          .typeString(`Fecha [${day}/${month}/${year+1000}]`)
          // .pauseFor(2000)
          .typeString('<br>')
          .typeString('Protocolo de diagnostico penitenciario ADX FLORENCE')
          // .pauseFor(2000)
          .typeString('<br>')
          .typeString('Inicializando')
          // .typeString('  ')
          // .changeDelay(600)
          // .typeString('. . .')
          // .pauseFor(500)
          .typeString('<br>')
          .changeDelay(50)
          .typeString('<span>Hola mundo!</span>')
          .pauseFor(2000)
          .start();
        }}/>
    </MainContainer>
  )
}



export default Intro

const MainContainer = styled.div`
  display: inline-block;
  height: 93vh;
  width: 100vh;
  margin-top: 2%;
  margin-left: 3%;
  color: white;
  font-size: 1.2rem;
`
