import React from 'react'
import styled from 'styled-components'
import Typewriter from 'typewriter-effect';

function Intro (){
  return(
    <MainContainer>
      <Typewriter
        onInit={(tw) => {
          tw.changeDelay(50)
          .changeDeleteSpeed(0.1)
          .typeString('Hello World!')
          .callFunction(() => {
          })
          .typeString('  ')
          .changeDelay(500)
          .typeString('. . .')
          .deleteChars(5)
          .typeString('  ')
          .typeString('. . .')
          .deleteChars(5)
          .typeString('  ')
          .typeString('. . .')
          .deleteChars(5)
          .pauseFor(2000)
          .typeString('<br>')
          .changeDelay(50)
          .typeString('Hola Mundo!')
          .pauseFor(2000)
          .start();
        }}/>
    </MainContainer>
  )
}

export default Intro

const MainContainer = styled.div`
  display: inline-block;
  height: 93vh;
  width: 100vh;
  margin-top: 2%;
  margin-left: 3%;
  background-color: grey;
  color: white;
  font-size: 1.2rem;
`
